jest.autoMockOff();
require(path(pathMap.components, "navpanel/navigation"));

describe("navigation", function() {
  var rendered, clickHandler;
  PG.Components.NavigationItem = genMockComponent();

  beforeEach(function(){
    clickHandler = jest.genMockFunction();
    rendered = utils.renderIntoDocument(<PG.Components.Navigation />)
  });

  afterEach(function(){
    clickHandler.mockClear();
  });

  it("renders the correct number of children", function() {
    rendered.setProps({items: [
      {text: "Sheets"},
      {text: "RFIs"}
    ]});

    var items = utils.scryRenderedComponentsWithType(rendered,PG.Components.NavigationItem);
    expect(items.length).toBe(2);
  });

});