var resolve = require.requireActual("path").resolve;
var _inspect_ = require.requireActual("util").inspect;

// convenience methods for absolute paths to something in the components dir
pathMap = {
  components:  resolve(__dirname, "./../../components"),
  javascripts: resolve(__dirname, "./../../javascripts")
};

path = function(root, path) {
  return resolve(root, path);
};

// a convenience for logging an object with nodes util.inspect
// obj is obv the target, opts per the node method -- see the node docs
inspect = function(obj, opts) {
  console.log(_inspect_(obj, opts));
};

// global namespace for components
require.requireActual(path(pathMap.javascripts, "pg"));

React = require("react/addons");
utils = React.addons.TestUtils;

// React's test utils mock component method returns an obj and not a fn
// that wont work for child components in a jsx heirarchy, use this instead
genMockComponent = function() {
  return React.createClass({render: function() { return(<div></div>); }});
};
